import React, { useState } from 'react';
import axios from 'axios';
import './App.css';
import Header from './Header';
import Card from './Card';
import GetStarted from './GetStarted';
import { Routes, Route } from 'react-router-dom';

function App() {
    const [sentence, setSentence] = useState('');
    const [response, setResponse] = useState([]);

    const handleInputChange = (event) => {
        setSentence(event.target.value);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        axios.get(`http://127.0.0.1:5000/formal_words/${sentence}`)
            .then((res) => {
                const filteredResponse = res.data.filter(Boolean);
                setResponse(filteredResponse);
            })
            .catch((err) => console.log(err));
    };

    return (
        <div>
            <div>
                <Header />
                <Card />
            </div>

            <form onSubmit={handleSubmit}>
                <div style={{ display: 'flex', flexDirection: 'column' }}>
                <h4>How can I professionally say...</h4>
                    <label>
                        <textarea
                            value={sentence}
                            onChange={handleInputChange}
                            style={{ width: '99%', height: '100px', resize: 'none', overflowWrap: 'break-word', verticalAlign: 'top' }}
                        />
                    </label>
                    <button type="submit" className="button button1" >Submit</button>
                </div>
            </form>
            {response.length > 0 && (
                <div className="output-container">
                <ol>
                    {response.map((step, index) => (
                        <li key={index}>
                            {step.replace(/^\d+\.\s*/, '')}
                        </li>
                    )).filter(Boolean)}
                </ol>
                <p>Not satisfied? Click on Submit again.</p>
                </div>
            )}
        </div>
    );

    return (
        <Routes>
            <Route path="/getstarted" element={<GetStarted />} />
        </Routes>
    );
}

export default App;
