import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
    const [sentence, setSentence] = useState('');
    const [response, setResponse] = useState([]);

    const handleInputChange = (event) => {
        setSentence(event.target.value);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        axios.get(`http://127.0.0.1:5000/formal_words/${sentence}`)
            .then((res) => {
                setResponse(res.data);
            })
            .catch((err) => console.log(err));
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
            <div>
                <label>
                        Enter a sentence:
                    <input type="text" value={sentence} onChange={handleInputChange} style={{ width: '80%', height: '100px', overflowWrap: 'break-word', verticalAlign: 'top' }} />
                </label>
            </div>
            <div>
                <button type="submit">Submit</button>
            </div>
            </form>
            <div>
                <ol>
                    {response.map((step, index) => (
                        <li key={index}>
                            {step.replace(/^\d+\.\s*/, '')}
                        </li>
                    ))}
                </ol>
            </div>

        </div>
    );
}

export default App;
